import * as bigintCryptoUtils from 'bigint-crypto-utils';
//import { BigInteger } from "big-integer";
export class Key {
    Key() { }
    async createPublicKey(upperlimit) {
        let p, q, m, n;
        //génération aléatoire de deux grands entiers premiers p et q
        let isPrime = true;
        do {
            isPrime = await bigintCryptoUtils.isProbablyPrime(upperlimit);
            p = bigintCryptoUtils.prime(upperlimit);
            q = bigintCryptoUtils.prime(upperlimit);
            p.then((v) => {
                console.log(v);
            });
        } while (!isPrime || p == q);
        // calcul de n = p * q
        //calcul de m (indicatrice d'Euler) = (p - 1) * (q - 1) tel qu'il soit premier et inférieur a n
        //choix d'un petit entier impair e (exposant public) qui soit premier avec m
    }
}
